import { Component } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss']
})
export class HomeComponent {

}
<figure class="figure image-central justify-content-center">
  <img src="https://maaretta.files.wordpress.com/2014/12/rickpotion-9_1.png" alt="starship" class="figure-img img-fluid rounded">
  <figcaption class="figure-caption text-end p-2">Acompanhe malucas viagens no tempo-espaço e por universos paralelos com Rick, um cientista com problemas com a bebida, e seu neto Morty, um adolescente não tão brilhante quanto o avô.</figcaption>
</figure>
