import { ApiService } from './../../service/api.service';
import { Component, OnInit } from '@angular/core';
import { Subject } from 'rxjs';
import { Results } from 'src/app/models/api-return';

@Component({
  selector: 'app-list',
  templateUrl: './list.component.html',
  styleUrls: ['./list.component.scss']
})
export class ListComponent implements OnInit{

  public resultList$ = new Subject<Results[] | undefined>();

  constructor(public service: ApiService){}

  ngOnInit(): void {
    this.service.getCharacterList().subscribe(
      (resp) => {
        console.log(resp);
        this.resultList$.next(resp.results);
      }
    )
  }
}
<div class="row row-cols-1 row-cols-md-3">
  <div class="col"  *ngFor="let item of resultList$ | async">
    <div class="card">
      <img src="{{ item.image }}" class="card-img-top" alt="{{ item.name }}">
      <div class="card-body">
        <p class="card-title">Name: {{ item.name | titlecase }}</p>
        <p class="card-text">Species: {{ item.species }}</p>
        <p class="card-text">Status: {{ item.status }}</p>
      </div>
    </div>
  </div>
</div>
