import { ApiReturn, Results } from './../../models/api-return';
import { ApiService } from './../../service/api.service';
import { Component, OnInit } from '@angular/core';
import { Observable } from 'rxjs';

@Component({
  selector: 'app-list',
  templateUrl: './list.component.html',
  styleUrls: ['./list.component.scss']
})
export class ListComponent implements OnInit {

  public pkmnList = new Observable<ApiReturn>();
  public resultList: Results[] | undefined = [];

  constructor(public service: ApiService) {}

  ngOnInit(): void {
    this.pkmnList = this.service.getPokemonList();

    this.pkmnList.subscribe(
      (resp) => {
        console.log(resp);
        this.resultList = resp.results;
      }
    )
  }
}
<div class="row">
  <ul class="list-goup">
    <li *ngFor="let item of resultList" class="list-item">
      <span class="col-2"> {{ item.name }}</span>
      <span class="col"> {{ item.url }} </span>
    </li>
  </ul>
</div>
