import { ApiReturn, Results } from './../../models/api-return';
import { ApiService } from './../../service/api.service';
import { Component, OnInit } from '@angular/core';
import { Observable, Subject } from 'rxjs';

@Component({
  selector: 'app-list',
  templateUrl: './list.component.html',
  styleUrls: ['./list.component.scss']
})
export class ListComponent implements OnInit {

  public resultList$ = new Subject<Results[] | undefined>();

  constructor(public service: ApiService) {}

  ngOnInit(): void {
    this.service.getPokemonList().subscribe(
      (resp) => {
        console.log(resp);
        this.resultList$.next(resp.results);
      }
    )
  }
}
<div class="row">
  <div *ngFor="let item of resultList$ | async" class="col-4 m-1">
    <div class="card" style="width: 18rem;">
      <!-- <img src="..." class="card-img-top" alt="..."> -->
      <div class="card-body">
        <h5 class="card-title">{{ item.name | titlecase }}</h5>
        <a routerLink="/detail/{{ item.name }}" class="btn btn-outline-secondary">See Details</a>
      </div>
    </div>
  </div>
</div>
