import { ApiService } from './../../service/api.service';
import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Pokemon } from 'src/app/models/api/pokemon-model';

@Component({
  selector: 'app-details',
  templateUrl: './details.component.html',
  styleUrls: ['./details.component.scss']
})
export class DetailsComponent implements OnInit {

  public pokemon = new Pokemon({});
  constructor(public service: ApiService, public route: ActivatedRoute) { }

  ngOnInit(): void {
    let paramName = this.route.snapshot.paramMap.get('name');
    if(paramName){
      // buscar o pokemon por nome
      this.service.getPokemonByName(paramName).subscribe(
        (resp) => {
          this.pokemon = resp;
        }
      );
    }
  }
}
<div class="row">
  <div class="row mb-2">
    <h1 class="card-title">{{ pokemon.name| titlecase }}</h1>
    <hr>
  </div>
  <div class="col-4">
    <div class="card" style="width: 18rem;">
      <img style="max-width: 10rem;" src="https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/versions/generation-v/black-white/animated/{{pokemon.id}}.gif" class="card-img-top" alt="...">
    </div>
  </div>
  <div class="col-4">
    <div class="card d-flex justify-content-center" style="width: 18rem;">
      <img style="max-width: 9rem;" src="https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/versions/generation-v/black-white/animated/back/{{pokemon.id}}.gif" class="card-img-top" alt="...">
    </div>
  </div>
  <div class="col-4">
    <div class="card" style="width: 18rem;">
      <img style="max-width: 10rem;" src="https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/versions/generation-v/black-white/animated/shiny/{{pokemon.id}}.gif" class="card-img-top" alt="...">
    </div>
  </div>
</div>
